    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.mylistactivity);
        lv = (ListView) findViewById(R.id.listView_item);
        //lv = (ListView) findViewById(R.id.lv_item);

        // setOn..() listener ends here, lv s
        int kk = 0;
        for ( kk=0; kk < schools.length; kk++ ) {
             //id2IntMap.put(schools[kk], String(kk) ); 
             id2IntMap.put(schools[kk], Integer.toString(kk) ); 
        } 
 
        inputSearch = (EditText) findViewById(R.id.editTextSearch);

        getMyValue();

        adapter = new CustomAdapter(MainActivity.this, R.layout.listview_row,
                searchResults);
        lv.setAdapter(adapter);
        //lst.setAdapter(adapter);
         
        lv.setOnItemClickListener(new AdapterView.OnItemClickListener() {
            @Override
            public void onItemClick(AdapterView<?> parent, View view, int position, long id) {
                String words[] = values[position].split(","); 

                String dis = ""; 
                for (int jj=0; jj < words.length; jj++) {
                   if ( jj>=title.length+1 ) continue;
                    dis += title[jj+1] +": " + words[jj] + "\n";  
                };

             //Toast.makeText(getApplicationContext(),"Clicked on "+values[position],Toast.LENGTH_SHORT).show();
                Toast.makeText(getApplicationContext(),"Clicked on :\n" + dis, Toast.LENGTH_SHORT).show();
            }
        });
        

        inputSearch.addTextChangedListener(new TextWatcher() {

            @Override
            public void onTextChanged(CharSequence cs, int arg1, int arg2,
                                      int arg3) {
                // get the text in the EditText
                String searchString = inputSearch.getText().toString();
                int textLength = searchString.length();
                searchResults.clear();

                for (int i = 0; i < originalValues.size(); i++) {
                    String wCode = originalValues.get(i).get("pname")
                            .toString();
                    if (textLength <= wCode.length()) {
                        // compare the String in EditText with Names in the
                        // ArrayList
                        if (searchString.equalsIgnoreCase(wCode.substring(0,
                                textLength)))
                            searchResults.add(originalValues.get(i));
                    }
                }

                adapter.notifyDataSetChanged();

            }

            @Override
            public void beforeTextChanged(CharSequence arg0, int arg1,
                                          int arg2, int arg3) {
                // TODO Auto-generated method stub

            }

            @Override
            public void afterTextChanged(Editable arg0) {
                // TODO Auto-generated method stub
            }
        });



    }

    private void getMyValue(){
        originalValues.clear();

        for (int j=0;j<schools.length;j++){

            temp = new HashMap<String, Object>();
            temp.put("pname",schools[j]);

            // add the row to the ArrayList
            originalValues.add(temp);
        }

        searchResults = new ArrayList<HashMap<String, Object>>(originalValues);
    }

    // define your custom adapter
    private class CustomAdapter extends ArrayAdapter<HashMap<String, Object>> {
        LayoutInflater inflater;

        public CustomAdapter(Context context, int textViewResourceId,
                             ArrayList<HashMap<String, Object>> Strings) {
            super(context, textViewResourceId, Strings);
            inflater = (LayoutInflater) context
                    .getSystemService(Context.LAYOUT_INFLATER_SERVICE);
        }

        // class for caching the views in a row
        private class ViewHolder {

            TextView productNameTxt;
        }

        ViewHolder viewHolder;

        @Override
        public View getView(int position, View convertView, ViewGroup parent) {

            if (convertView == null) {

                convertView = inflater.inflate(R.layout.listview_row, null);
                viewHolder = new ViewHolder();


                viewHolder.productNameTxt = (TextView) convertView
                        .findViewById(R.id.product_name);


                // link the cached views to the convertview
                convertView.setTag(viewHolder);

            } else
                viewHolder = (ViewHolder) convertView.getTag();

            viewHolder.productNameTxt.setText(searchResults.get(position).get("pname")
                    .toString());

            // return the view to be displayed
            return convertView;
        }
    }


